def calculate_flesch_kincaid_grade_level(text):
    words = text.split()
    sentences = text.count('.') + text.count('!') + text.count('?')
    syllables = 0
    vowels = 'aeiouy'
    for word in words:
        word = word.lower()
        count = 0
        for letter in word:
            if letter in vowels:
                count += 1
        if word.endswith('e'):
            count -= 1
        if count > 0:
            syllables += 1
        else:
            syllables += 1 if word.endswith(('d', 'ch','sh', 'th')) else 0
    return round(0.39 * (len(words) / sentences) + 11.8 * (syllables / len(words)) - 15.59, 2)

